import React, { useEffect } from 'react'
// import styled from 'styled-components'
import { useGlobalContext } from '../../context/globalContext';
import make_prediction from '../../Algorithm/linear_regression'

function Prediction() {
    const {incomes, expenses} = useGlobalContext()
    const training_data = [incomes,expenses]
    const predicted = make_prediction(training_data,12000)


    return(
        <>

        <h4>Your expense next month will be</h4>
        {predicted}
        

        </>
    )
}

export default Prediction;
